}

	if(action == 'emp'){
				swal({
  title: 'Sign In',
  html:
    'Employee ID <input id="swal-input1" class="swal2-input">',
  preConfirm: function () {
    return new Promise(function (resolve) {
      resolve([
        $('#swal-input1').val()
      ])
   input1 = $('#swal-input1').val();

		})
